import React from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/styles';
import {
  Card,
  CardContent,
  Typography,
} from '@material-ui/core';

const useStyles = makeStyles(theme => ({
  root: {},
  details: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  priceperday: {
    marginLeft: 'auto',
  },
  suit: {
    display: 'flex',
    justifyContent: 'flex-start',
    alignItems: 'baseline',
    textTransform: 'capitalize'
  },
  suittype: {
    marginLeft: 10,
    textTransform: 'uppercase'
  }
}));

const ProductListDetailsHeader = props => {
  const { className, ...rest } = props;
  

  const classes = useStyles();

  return (
    <Card
      {...rest}
      className={clsx(classes.root, className)}
    >
      {props.listingsData.map(lists => lists._id === props.id ? <CardContent key={lists._id}>
        <div className={classes.details}>
          <div>
            <div className={classes.suit}>
            <Typography
              gutterBottom
              variant="h4"
            >
              {lists.title}
            </Typography>
            <Typography
              className={classes.suittype}
              color="textSecondary"
              variant="body2"
            >
              {lists.brand}
            </Typography>
            </div>
            <Typography
              className={classes.dateText}
              color="textSecondary"
              variant="body1"
            >
              {lists.size} | {lists.color} | {lists.category} | {lists.condition} | {lists.occasion}
            </Typography>
          </div>
          <Typography
              className={classes.priceperday}
              color="primary"
              variant="h3"
            >
              ${lists.price} / day
            </Typography>
        </div>
      </CardContent> : '' )}
      
    </Card>
  );
};

ProductListDetailsHeader.propTypes = {
  className: PropTypes.string
};

export default ProductListDetailsHeader;
